$(document).ready(function(){var t=$(".isotope").isotope({itemSelector:".element-item",layoutMode:"fitRows",getSortData:{name:".name",weight:function(t){var e=$(t).find(".weight").text();return parseFloat(e.replace(/[\(\)]/g,""))}}}),e={numberGreaterThan50:function(){var t=$(this).find(".number").text();return parseInt(t,10)>50},ium:function(){var t=$(this).find(".name").text();return t.match(/ium$/)}};$("#filters").on("click","button",function(){var n=$(this).attr("data-filter");n=e[n]||n,t.isotope({filter:n})}),$("#sorts").on("click","button",function(){var e=$(this).attr("data-sort-by");t.isotope({sortBy:e})}),$(".button-group").each(function(t,e){var n=$(e);n.on("click","button",function(){n.find(".is-checked").removeClass("is-checked"),$(this).addClass("is-checked")})})});